/**
 * A department model
 *
 * @param  {String}         id              The ID of the department
 * @param  {String}         tenantAlias     The alias of the tenant this department is being created in
 * @param  {String}         displayName     The name of the department (e.g. Department of Zoology)
 * @return {Department}                     Object representing the created department
 */
var Department = module.exports.Department = function(id, tenantAlias, displayName) {
    var that = {};
    that.id = id;
    that.tenantAlias = tenantAlias;
    that.displayName = displayName;
    that.resourceType = 'department';
    return that;
};
