var OAE = require('oae-util/lib/oae');

var DepartmentsAPI = require('./api');

/**
 * Function that imports a CSV file containing all the departments
 */
var importDepartments = function(req, res) {
    DepartmentsAPI.importDepartments(req.ctx, req.files.departments, function(err) {
        if (err) {
            res.send(err.code, err.msg);
        }

        res.send(200);
    });
};
OAE.globalAdminRouter.on('post', '/api/departments/import', importDepartments);
OAE.tenantRouter.on('post', '/api/departments/import', importDepartments);
