var OAE = require('oae-util/lib/oae');

var DepartmentsAPI = require('./api');

/*!
 * Import a CSV file containing all the departments on the global admin router
 *
 *  * The tenant alias should always be specified
 *  *
 *  * 'data': {
 *  *     'tenantAlias': 'tenantAlias'
 *  * }
 *
 *  _POST_ `/api/departments/import`
 */
OAE.globalAdminRouter.on('post', '/api/departments/import', function(req, res) {
    if (!req.body.tenantAlias) {
        return res.send(400, 'Invalid or no tenant specified');
    }

    DepartmentsAPI.importDepartments(req.ctx, req.body.tenantAlias, req.files.departments, function(err) {
        if (err) {
            res.send(err.code, err.msg);
        }

        res.send(200);
    });
});

/*!
 * Import a CSV file containing all the departments on the tenant router
 *
 *  _POST_ `/api/departments/import`
 */
OAE.tenantRouter.on('post', '/api/departments/import', function(req, res) {
    if (!req.ctx && !req.ctx.tenant()) {
        return res.send(400, 'Invalid or no tenant specified');
    }

    DepartmentsAPI.importDepartments(req.ctx, req.ctx.tenant().alias, req.files.departments, function(err) {
        if (err) {
            res.send(err.code, err.msg);
        }

        res.send(200);
    });
});
