/**
 * A journal model
 *
 * @param  {String}     id              The ID of the journal
 * @param  {String}     displayName     The display name of the journal
 * @return {Journal}                    Object representing a journal
 */
var Journal = module.exports.Journal = function(id, displayName) {
    var that = {};
    that.id = id;
    that.displayName = displayName;
    that.resourceType = 'journal';
    return that;
};

