var OAE = require('oae-util/lib/oae');

var JournalsAPI = require('oae-journals');

/**
 * Function that imports a CSV file containing all the journals
 */
var importJournals = function(req, res) {
    JournalsAPI.importJournals(req.ctx, req.files.journals, function(err) {
        if (err) {
            res.send(err.code, err.msg);
        }

        res.send(200);
    });
};
OAE.globalAdminRouter.on('post', '/api/journals/import', importJournals);
OAE.tenantRouter.on('post', '/api/journals/import', importJournals);
