var RestUtil = require('./util');

/**
 * Import a collection of departments from a CSV-file
 *
 * @param  {Context}        restCtx                 Standard REST Context object that contains the current tenant URL and the current user credentials
 * @param  {Object}         departmentsCSV          File object representing the uploaded CSV file as returned by Express
 * @param  {Function}       callback                Standard callback function
 * @param  {Object}         callback.err            Standard error object, if any
 */
var importDepartments = module.exports.importDepartments = function(restCtx, departmentsCSV, callback) {
    RestUtil.RestRequest(restCtx, '/api/departments/import', 'POST', departmentsCSV, callback);
};
