var OAE = require('oae-util/lib/oae');
var Tickets = require('./api');

/////////////
// Tenants //
/////////////

/*!
 * Returns a collection of tickets
 *
 * Rest Endpoint:
 *
 * _GET_ `/api/tickets`
 *
 * * returns a array of JSON object of tickets
 */
var getTickets = function(req, res) {
    Tickets.getTickets(req.ctx, function(err, tickets) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, tickets);
    });
};
OAE.globalAdminRouter.on('get', '/api/tickets', getTickets);
OAE.tenantRouter.on('get', '/api/tickets', getTickets);

/*!
 * Returns a specified ticket
 *
 * Rest Endpoint:
 *
 * _GET_ `/api/ticket/:ticketId`
 *
 * * returns a JSON object of a ticket
 */
var getTicket = function(req, res) {
    Tickets.getTicket(req.ctx, req.params.ticketId, function(err, ticket) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, ticket);
    });
};
OAE.globalAdminRouter.on('get', '/api/ticket/:ticketId', getTicket);
OAE.tenantRouter.on('get', '/api/ticket/:ticketId', getTicket);

/*!
 * Creates a ticket
 *
 * Rest Endpoint:
 *
 * _POST_ `/api/ticket/:ticketId/create`
 *
 * * returns a JSON object of a ticket
 */
var createTicket = function(req, res) {
    var opts = {
        'creatorId': '',
        'resourceId': '',
        'tenantAlias': req.ctx.user().tenant.alias
    };
    Tickets.createTicket(req.ctx, opts, function(err, ticket) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, ticket);
    });
};
OAE.globalAdminRouter.on('post', '/api/ticket/create', createTicket);
OAE.tenantRouter.on('post', '/api/ticket/create', createTicket);

/*!
 * Deletes a specified ticket
 *
 * Rest Endpoint:
 *
 * _POST_ `/api/ticket/:ticketId/delete`
 */
var deleteTicket = function(req, res) {
    Tickets.deleteTicket(req.ctx, req.params.ticketId, function(err) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200);
    });
};
OAE.globalAdminRouter.on('post', '/api/ticket/:ticketId/delete', deleteTicket);
OAE.tenantRouter.on('post', '/api/ticket/:ticketId/delete', deleteTicket);

/*!
 * Updates a specified ticket
 *
 * Rest Endpoint:
 *
 * _POST_ `/api/ticket/:ticketId/update`
 *
 * * returns a JSON object an updated ticket
 */
var updateTicket = function() {
    Tickets.updateTicket(req.ctx, req.params.ticketId, req.body, function(err, ticket) {
        if (err) {
            return res.send(err.code, err.msg);
        }
        res.send(200, ticket);
    });
};
OAE.globalAdminRouter.on('post', '/api/ticket/:ticketId/update', updateTicket);
OAE.tenantRouter.on('post', '/api/ticket/:ticketId/update', updateTicket);
