/**
 * Determine whether of not the given id represents an external id
 *
 * @param  {String}     isExternalId    The external id to check
 * @return {Boolan}                     Whether of not the id is an external id
 */
var isExternalId = module.exports.isExternalId = function(externalId) {
    return externalId.indexOf('OA-') === 0;
};

/**
 * Determine whether or not the given id represents a ticket
 *
 * @param  {String}     ticketId        The ticket id to check
 * @return {Boolean}                    Whether or not the id is a ticket id
 */
var isTicketId = module.exports.isTicketId = function(ticketId) {
    return ticketId.indexOf('t:') === 0;
};
